// import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';


function App() {
  const [cocktails, setCocktails] = useState(null);
  useEffect(() => {
    console.log("Fetching data...");
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/fetch_random/');
        console.log(response.data);
        setCocktails(response.data.drinks);
      } catch (error) {
        console.log("An error occurred:", error);
      }
    };
    fetchData();
  }, []);
  const fetchDataAgain = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/fetch_random/');
      console.log(response.data);
      setCocktails(response.data.drinks);
    } catch (error) {
      console.log("An error occurred:", error);
    }
  };  
  
  const getIngredients = (cocktail) => {
    let ingredientsList = [];
    for (let i = 1; i <= 15; i++) { // Loop through possible 15 ingredients
      const ingredient = cocktail[`strIngredient${i}`];
      const measure = cocktail[`strMeasure${i}`];
      if (ingredient) {
        ingredientsList.push(<li className="list-group-item list-group-item-action" key={ingredient}  >{measure} {ingredient}</li>);
      }
    }
    return ingredientsList;
  };

  return (
    <div className="container mt-5 text-center">  
      {cocktails ? cocktails.map((cocktail, index) => (
        <div key={index} className="card">
          <button
              className="btn btn-primary"
              onClick={fetchDataAgain}
            >
              Get New Cocktail
            </button>
          <h1>{cocktail.strDrink}</h1>
          <img src={cocktail.strDrinkThumb} alt={cocktail.strDrink} width='250' className="rounded mx-auto d-block" />

          <div className="recipe">
            <h3>Ingredients:</h3>
            <ul className="list-group list-group-flush"> 
              {getIngredients(cocktail)}
            </ul>
          </div>
          <h3> Recipe </h3>
          <p>{cocktail.strInstructions}</p>
        </div>
      )) : "Loading..."}
    </div>
  );
}
export default App;
