import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [cocktail, setCocktail] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/fetch_random/');
      setCocktail(response.data);  // Update this line to set the cocktail directly
    } catch (error) {
      console.error("An error occurred:", error);
    }
  };

  const getIngredients = (cocktail) => {
    let ingredientsList = [];
    // The ingredients and measures are now arrays
    cocktail.ingredients.forEach((ingredient, index) => {
      const measure = cocktail.measures[index];
      ingredientsList.push(
        <li className="list-group-item list-group-item-action" key={ingredient}>
          {measure} {ingredient}
        </li>
      );
    });
    return ingredientsList;
  };

  return (
    <div className="container mt-5 text-center">
      {cocktail ? (
        <div className="card">
          <button className="btn btn-primary" onClick={fetchData}>
            Get New Cocktail
          </button>
          <h1>{cocktail.name}</h1>
          <img src={cocktail.image} alt={cocktail.name} width='250' className="rounded mx-auto d-block" />
          <div className="recipe">

            <h3><br></br>Glass Type:</h3>
            <p>{cocktail.glass}</p>
            <h3>Ingredients:</h3>
            <ul className="list-group list-group-flush">
              {getIngredients(cocktail)}
            </ul>
          </div>
          <h3>Recipe</h3>
          <p>{cocktail.instructions}</p>
        </div>
      ) : "Loading..."}
    </div>
  );
}

export default App;
